import './NewExpenses.css'
import ExpenseForm from './ExpenseForm'

const NewExpense = (props) => {
  const newExpenseData = (newExpense) => {
    const newEnteredData = {
      ...newExpense,
      id: Math.random().toString()
    }
    props.appendAllData(newEnteredData)
  }
  function changeRenderFormOrBtn() {
    renderFormOrBtn = <ExpenseForm newExpenseData={newExpenseData} />
  }
  let renderFormOrBtn = <button onClick={changeRenderFormOrBtn}>Add new expense</button>
    return (
        <div className="new-expense">
          {renderFormOrBtn}
        </div>
    )
}

export default NewExpense
